function run(apiResponse) {
  try {
    console.log("Entrada recebida: ", apiResponse);

    const data = typeof apiResponse === 'string' ? JSON.parse(apiResponse) : apiResponse;

    console.log("Dados parseados: ", data);

    const carousel = {
      "type": "application/vnd.lime.container+json",
      "value": {
        "type": "carousel",
        "content": data.map(repo => ({
          "type": "application/vnd.lime.document-select+json",
          "value": {
            "header": {
              "type": "application/vnd.lime.media-link+json",
              "value": {
                "title": repo.fullName, 
                "text": repo.description, 
                "type": "image/jpeg", 
                "uri": repo.avatarUrl 
              }
            }
          }
        }))
      }
    };

    console.log("Carrossel gerado: ", JSON.stringify(carousel));

    return JSON.stringify(carousel);
  } catch (error) {
    console.log("Erro durante a execu√ß√£o do script:", error);
    return null; 
  }
}
